template.script:
function previewImages(event) {
    var files = event.target.files;
    var output = document.querySelector('#image_preview>.row');

    for (var i = 0; i < files.length; i++) {
        var reader = new FileReader();
        reader.onload = function(e) {
            // Tạo thumbnail cho ảnh
            var img = document.createElement('img');
            img.classList.add('img-thumbnail');
            img.src = e.target.result;
            img.style.maxWidth = "400px";
            img.style.maxHeight = "200px";

            // Tạo input tên cho ảnh
            var nameInput = document.createElement('input');
            nameInput.classList.add('form-control');
            nameInput.type = 'text';
            nameInput.name = 'image_names[]';  // Đảm bảo sử dụng mảng để gửi nhiều tên ảnh
            nameInput.required = '1';  // Đảm bảo sử dụng mảng để gửi nhiều tên ảnh
            nameInput.placeholder = 'Tên ảnh';  // Placeholder cho mỗi trường nhập tên
            nameInput.classList.add('mt-2');

            // Tạo form group cho ảnh và input tên
            var nameGroup = document.createElement('div');
            nameGroup.classList.add('col-4');
            nameGroup.classList.add('mt-2');

            // Tạo div chứa ảnh và input tên trong cùng một cột
            var div = document.createElement('div');
            div.classList.add('text-center');
            div.appendChild(img);
            div.appendChild(nameInput);
            nameGroup.appendChild(div);

            // Thêm nhóm ảnh và input tên vào phần hiển thị
            output.appendChild(nameGroup);
        };
        reader.readAsDataURL(files[i]);
    }
}
